import React, { useRef, useState, useEffect, useCallback } from "react";
import { useTransition, animated } from "react-spring";
import "./WordTransition.css";

function WordTransition() {
  const ref = useRef([]);
  const [items, set] = useState([]);
  const transitions = useTransition(items, null, {
    from: {
      opacity: 0,
      height: 0,
      innerHeight: 0,
      transform: "perspective(600px) rotateX(0deg)",
      color: "#fff",
    },
    enter: [
      { opacity: 1, height: 80, innerHeight: 80 },
      { transform: "perspective(600px) rotateX(180deg)", color: "#dbb700" },
      { transform: "perspective(600px) rotateX(0deg)" },
    ],
    leave: [
      { color: "#19a9bc" },
      { innerHeight: 0 },
      { opacity: 0, height: 0 },
    ],
    update: { color: "#0f0a10" },
  });

  const reset = useCallback(() => {
    ref.current.map(clearTimeout);
    ref.current = [];
    set([]);
    ref.current.push(
      setTimeout(() => set(["frontend", "Software", "Backend"]), 2000)
    );
    ref.current.push(setTimeout(() => set(["frontend", "Backend"]), 5000));
    ref.current.push(
      setTimeout(() => set(["frontend", "Developer", "Backend"]), 8000)
    );
  }, []);

  useEffect(() => void reset(), [reset]);

  return (
    <div>
      {transitions.map(({ item, props: { innerHeight, ...rest }, key }) => (
        <animated.div
          className="transitions-item"
          key={key}
          style={rest}
          onClick={reset}
        >
          <animated.div style={{ overflow: "hidden", height: innerHeight }}>
            {item}
          </animated.div>
        </animated.div>
      ))}
    </div>
  );
}

export default WordTransition;
